	public function __construct()
	{
		parent::__construct();
	}

	public function __destruct()
	{
	}


	private function transcode_pre_style(&$page)
	{
		$this->get_style();

		$this->minify_media();
	}

	private function transcode_post_style(&$page)
	{
		$this->update_font_face();
	}


	private function transcode_pre_image(&$page)
	{
		$this->update_image($page);
	}

	private function transcode_post_image(&$page)
	{
	}


	private function transcode_sidebar(&$page)
	{
	}


	private function transcode_pre_html(&$page)
	{
		$this->transcode_pre_style($page);

		$this->transcode_pre_image($page);

		// Invalid URL protocol 'hhttps:' for attribute 'href' in tag 'a'.
		$page = str_replace('<a title="WordPress Foundation" href="hhttps://wordpressfoundation.org/" rel="nofollow">', '<a title="WordPress Foundation" href="https://wordpressfoundation.org/" rel="nofollow">', $page);
	}

	private function transcode_post_html(&$page)
	{
		$this->transcode_post_image($page);

		$this->transcode_post_style($page);

		$this->transcode_sidebar($page);
	}


	public function transcode(&$page, $home_url, $theme, $data)
	{
		$this->init($home_url, $data);

		$this->transcode_pre_html($page);
		$this->transcode_html($page);
		$this->transcode_post_html($page);

		$this->transcode_head($page);
	}
